sys_script.script:
(function executeRule(current, previous) {

	var gr = current.sysapproval.getRefRecord();
	if (gs.getUserID() == current.approver)
		gr.comments = "承認申請は" + current.comments.getDisplayValue() + "によって" + current.state.getDisplayValue()  + "とされました。";
	else
		gr.comments = "承認申請は" + current.state.getDisplayValue() + gs.getUserDisplayName()+ "によって代理で" + current.approver.getDisplayValue();

	gr.update();

})(current, previous);
