sp_widget.client_script:
function() {
  var c = this;
 c.uiAction = function(action) {
 c.data.action = action
 c.server.update().then(function() {
 c.data.action = '';
 })
 }
}

sp_widget.script:
(function() {

 // Get table & sys_id
 data.table = $sp.getParameter('table');
 data.sys_id = $sp.getParameter("sys_id");

	// Valid GlideRecord
	gr = new GlideRecord(data.table);
	if (!gr.isValid())
		return;
	// Valid sys_id
	if (!gr.get(data.sys_id))
		return;
  // input
  if (input && input.action) {
  var action = input.action;

  if (data.table == 'x_563475_3_kp1') {
		if (action == 'resubmit') {
			gr.setValue('state', 2);
			gr.setValue('approval', "申請済み");
			gr.setValue('comments',"再申請しましたー")
			gr.update();
		}
		if (action == 'cancel') {
			gr.setValue('state', -1);
			gr.setValue('approval', "却下");
			gr.setValue('comments',"取り下げましたー")
			gr.update();
		}
  if (data.table == 'sysapproval_approver') {
		if (action == 'resubmit') {
			gr.setValue('state', "requested");
			gr.update();
		}
  }
  }

  }})();
